import React, { useState } from 'react';

function Project(props) {
  const url = props.url;
  const imagePath = process.env.PUBLIC_URL + '/' + props.image;
  const [isLearnMoreOpen, setIsLearnMoreOpen] = useState(false);

  // Function to toggle the "Learn More" section
  const toggleLearnMore = () => {
    setIsLearnMoreOpen(!isLearnMoreOpen);
  };

  return (
    <div className='project'>
      <img src={imagePath} alt={props.alt} />
      <div id='project-info'>
        <p>{props.name}</p>
        <p>{props.description}</p>
        <a href={url} target="_blank" rel="noopener noreferrer">{props.url}</a>
        <button onClick={toggleLearnMore}>Learn More</button>
      </div>
      {isLearnMoreOpen && (
        <aside className="learn-more-section">
          {/* Add the content for the "Learn More" section here */}
          <p>Learn more about this project</p>
        </aside>
      )}
    </div>
  );
}

export default Project;
