import './App.css';

import ProjectList from './components/ProjectList/ProjectList.js';
import Sidebar from './components/Sidebar/Sidebar.js';
import Popup from './components/Popup/Popup.js';

function App() {

    const [isLearnMoreOpen, setIsLearnMoreOpen] = useState(false);

    // Function to toggle the "Learn More" section
    const toggleLearnMore = () => {
      setIsLearnMoreOpen(!isLearnMoreOpen);
    };


    return (
        <div className='app'>
            <Popup />
            <div className='app-container'>
                <h1>My projects</h1>
                <Sidebar />
                <main className='main-container'>
                    <ProjectList />


                    <button onClick={toggleLearnMore}>
                        {isLearnMoreOpen ? 'Hide Learn More' : 'Show Learn More'}
                    </button>
                    {isLearnMoreOpen && (
                        <aside className="learn-more-section">
                            {/* Add the content for the "Learn More" section here */}
                            {/* You can include any content you want to display */}
                        </aside>
                    )}



                </main>

            </div>

        </div>
    );
}

export default App;
